import React, {useState} from "react";
import axios from 'axios';
const FindJobsComponent = () => {
    const [searchData, setSearchData] = useState({
        jobTitle: "",
        jobType: "",
        jobLocation: "",
    });

     const handleChange = (e) => {
        const { name, value } = e.target;
        setSearchData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.get('http://localhost:8082/find-jobs', searchData)
            .then(response => {
                console.log("Job posted successfully:", response.data);
                // Add your logic here to handle successful response
            })
            .catch(error => {
                console.error("Error posting job:", error);
                // Add your logic here to handle error
            });
        setSearchData({ jobTitle: "",
        jobType: "",
        jobLocation: ""
        })
    }

    return(
        <div>
           <h1>Post Job</h1>
            <div>
                <form onSubmit={handleSubmit}>
                    <label>Job Title</label>
                    <input
                        type="text"
                        name="jobTitle"
                        value={searchData.jobTitle}
                        onChange={handleChange}
                    />
                    <label>Job Type</label>
                    <select
                        name="jobType"
                        value={searchData.jobType}
                        onChange={handleChange}
                    >
                        <option value="FULL_TIME">Full Time</option>
                        <option value="PART_TIME">Part Time</option>
                        <option value="INTERNSHIP">Internship</option>
                    </select>
                    <label>Job Location</label>
                    <input
                        type="text"
                        name="jobLocation"
                        value={searchData.jobLocation}
                        onChange={handleChange}
                    />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>    
    )
}
export default FindJobsComponent;
